import React, { useEffect , useState} from 'react'
import { TextField, Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/core'
import axios from 'axios'

const useStyles = makeStyles(()=>{});

const EmployeeAddPage = ()=>{
    const classes = useStyles();

    const [firstName, setFirstName] = useState("");
    const [middleName, setMiddleName] = useState("");
    const [lastName, setLastName] = useState("");
    const [birthDate, setBirthDate] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [employmentDate, setEmploymentDate] = useState("");
    const [position, setPosition] = useState("");
    const [hasAccessToDB, setHasAccessToDB] = useState("");
    const [remark, setRemark] = useState("");

    useEffect(()=>{
        axios.post("http://localhost:8080/employee/add",

        {
            "firstName" : firstName,
            "middleName" : middleName,
            "lastName" : lastName,
            "birthDate" : birthDate,
            "email" : email,
            "phoneNumber" : phoneNumber,
            "employmentDate" : employmentDate,
            "position" : position,
            "hasAccessToDB" : hasAccessToDB,
            "remark" : remark
        }

        )
            .then((response) => {
                const {data} = response
                 console.log(data);
                console.log(response.data)
                alert("Employee is Registered Successfully with info: "+data.message)
            })
            .catch((error) => {
                console.log(error);
                alert(error);
            })
        history.push('/')

    },[firstName]);

    return(
        <form>
            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"First Name"}
                placeholder = {"First Name"}
                variant = {"outlined"}
                value = {firstName}
                onChange = {(event)=>{
                    setFirstName(event.target.value);
                }} 
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Middle Name"}
                placeholder = {"middle Name"}
                variant = {"outlined"}
                value = {middleName}
                onChange = {(event)=>{
                    setMiddleName(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Last Name"}
                placeholder = {"Last Name"}
                variant = {"outlined"}
                value = {lastName}
                onChange = {(event)=>{
                    setLastName(event.target.value);
                }}
            />

            <TextField
                type = {"date"}
                autoFocus = {true}
                className = {classes.TextField}

                placeholder = {"birth date"}
                variant = {"outlined"}
                value = {birthDate}
                onChange = {(event)=>{
                    setBirthDate(event.target.value);
                }}
            />

            <TextField
                type = {"email"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Email"}
                placeholder = {"sth@sth.com"}
                variant = {"outlined"}
                value = {email}
                onChange = {(event)=>{
                    setEmail(event.target.value);
                }}
            />

            <TextField
                type = {"tel"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Mobile Phone Number"}
                placeholder = {"0912345678"}
                variant = {"outlined"}
                value = {lastName}
                onChange = {(event)=>{
                    setPhoneNumber(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Position"}
                placeholder = {"Security Guard"}
                variant = {"outlined"}
                value = {position}
                onChange = {(event)=>{
                    setPosition(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Access to Database"}
                placeholder = {"false or true"}
                variant = {"outlined"}
                value = {hasAccessToDB}
                onChange = {(event)=>{
                    setHasAccessToDB(event.target.value);
                }}
            />

            <Button
                className = {classes.Button}
                variant = {"contained"}
                color = {"primary"}
                onClick = {()=> console.log("button click")}
            >
                add employee
            </Button>

        </form>
    )
}

export default EmployeeAddPage
