import React, { useEffect , useState} from 'react'
import { TextField, Button} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import CustomerAddPage from "./CustomerAddPage";
import HeaderInAdminPage from "../../headers/HeaderInAdminPage";
import UsersMaterialListPage from "../../tables/usersPages/UsersMaterialListPage";
import CustomerBar from "./CustomerBar";
import SellerMaterialList from "./SellerMaterialList";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 15
    },

    Button : {
        margin : 18
    },
    boxBorder :{
        border : "gray 5px solid",
        marginTop:10
    },

}));

const SalesPage = ()=>{

    const baseUrl = "http://localhost:8080";

    const classes = useStyles();

    const [isSubmitClicked, setIsSubmitClicked] = useState(false);

    const [itemId, setItemId] = useState("");
    const [sellerEmployeeId, setSellerEmployeeId] = useState("");
    const [price, setPrice] = useState("");
    const [date, setDate] = useState("");


    useEffect(()=>{
        if(isSubmitClicked === true){
            axios.post("http://localhost:8080/sales/add",

                {
                    "itemId" : itemId,
                    "sellerEmployeeId" : sellerEmployeeId,
                    "price" : price,
                    "date" : date
                }

            )
                .then((response) => {
                    const {data} = response
                    console.log(data);
                    console.log(response.data)
                    alert("Employee is Registered Successfully with info: "+data.message)
                })
                .catch((error) => {
                    console.log(error);
                    alert(error);
                })
        }

    },[itemId,sellerEmployeeId,price]);

    return(
        <>
            <HeaderInAdminPage/>
            <hr/>
            <CustomerBar/>
            <CustomerAddPage/>
            <hr/>

        <div className={classes.boxBorder}>
            <Form>
                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Item Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {itemId}
                    onChange = {(event)=>{
                        setItemId(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Seller Employee Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {sellerEmployeeId}
                    onChange = {(event)=>{
                        setSellerEmployeeId(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Customer Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {sellerEmployeeId}
                    onChange = {(event)=>{
                        setSellerEmployeeId(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Price Sold At"}
                    placeholder = {"$4000.00"}
                    variant = {"outlined"}
                    value = {price}
                    onChange = {(event)=>{
                        setPrice(event.target.value);
                    }}
                />

                <TextField
                    type = {"date"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    variant = {"outlined"}
                    value = {date}
                    onChange = {(event)=>{
                        setDate(event.target.value);
                    }}
                />

                <Button
                    className = {classes.Button}
                    variant = {"contained"}
                    color = {"primary"}
                    onClick = {()=> setIsSubmitClicked(true)}
                >
                    sell
                </Button>

            </Form>
        </div>
            <SellerMaterialList/>
        </>

    )
}

export default SalesPage
