import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
//import {Alert,Navbar,NavbarBrand,NavItem,NavbarToggler,
 //   Nav, NavLink, NavbarText,Collapse} from 'reactstrap';
import {Button, Label, Form, Input, FormGroup, Row, Col} from 'reactstrap';
import axios from 'axios'

const instance = axios.create({
    baseURL:'http://localhost:8080',
    headers:{'Content-Type':'application/json'}
})

class EmployeeRegistrationForm extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            firstName : "",
            middleName : "",
            lastName : "",
            phoneNumber : "",
            email : "",
            birthDate : "",
            employmentDate : "",
            position : "",
            remark : "",
            hasAccessToDB : ""
        }
        this.handleChangePhoneNumber = this.handleChangePhoneNumber.bind(this)
        this.handleChangeFirstName = this.handleChangeFirstName.bind(this)
        this.handleChangeLastName = this.handleChangeLastName.bind(this)
        this.handleChangeMiddleName = this.handleChangeMiddleName.bind(this)
        this.handleChangeEmail = this.handleChangeEmail.bind(this)
        this.handleChangeBirthDate = this.handleChangeBirthDate.bind(this)
        this.handleChangeEmploymentDate = this.handleChangeEmploymentDate.bind(this)
        this.handleChangePosition = this.handleChangePosition.bind(this)
        this.handleChangeRemark = this.handleChangeRemark.bind(this)
        this.handleChangeHasAccessToDB = this.handleChangeHasAccessToDB.bind(this)
        this.handleAddEmployee = this.handleAddEmployee.bind(this)
    }

    handleAddEmployee(){
        instance({
            'method':'POST',
            'url':'/employee/add',
            'data':{
                middleName:this.state.middleName,
                firstName:this.state.firstName,
                lastName:this.state.lastName,
                email:this.state.email,
                birthDate: this.state.birthDate,
                employmentDate: this.state.employmentDate,
                position: this.state.position,
                phoneNumber: this.state.phoneNumber,
                remark: this.state.remark,
                hasAccessToDB: this.state.hasAccessToDB
            }
        })
    }


    // handleLogin(){
    //     instance({
    //         'method':'POST',
    //         'url':'/api/login',
    //         'data':{
    //             username:this.state.username,
    //             password:this.state.password
    //         }
    //     })
    // }

    handleChangeMiddleName(event){
        const md = event.target.value;
        this.setState({username:md})
    }
    handleChangeFirstName(event){
        const first_name = event.target.value;
        this.setState({firstName:first_name})
    }
    handleChangeLastName(event){
        const last_name = event.target.value;
        this.setState({lastName:last_name})
    }
    handleChangeEmail(event){
        const email = event.target.value;
        this.setState({email:email})
    }
    handleChangeBirthDate(event){
        const bd = event.target.value;
        this.setState({birthDate : bd})
    }
    handleChangeEmploymentDate(event){
        const ed = event.target.value;
        this.setState({employmentDate : ed})
    }
    handleChangePosition(event){
        const po = event.target.value;
        this.setState({position : po})
    }
    handleChangePhoneNumber(event){
        const pn = event.target.value;
        this.setState({phoneNumber:pn})
    }
    handleChangeRemark(event){
        const re = event.target.value;
        this.setState({remark:re})
    }
    handleChangeHasAccessToDB(event){
        const acc = event.target.value;
        this.setState({hasAccessToDB:acc})
    }


    render(){
        return(
            <div>
                <Row>
                    <Col lg={
                        {offset:4,size:3}
                    } md={4} sm={6} xs={12}
                    >

                        <Form onSubmit={this.handleAddEmployee}>
                            <FormGroup>
                                <Label>Middle Name:</Label>
                                <Input type="text" value={this.state.middleName} onChange={this.handleChangeMiddleName}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>First Name:</Label>
                                <Input type="text" value={this.state.firstName} onChange={this.handleChangeFirstName}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Last Name:</Label>
                                <Input type="text" value={this.state.lastName} onChange={this.handleChangeLastName}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Email:</Label>
                                <Input type="text" value={this.state.email} onChange={this.handleChangeEmail}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Birth Date:</Label>
                                <Input type="text" value={this.state.birthDate} onChange={this.handleChangeBirthDate}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Employment Date:</Label>
                                <Input type="text" value={this.state.employmentDate} onChange={this.handleChangeEmploymentDate}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>position:</Label>
                                <Input type="text" value={this.state.position} onChange={this.handleChangePosition}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Phone Number:</Label>
                                <Input type="text" value={this.state.phoneNumber} onChange={this.handleChangePhoneNumber}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Remark:</Label>
                                <Input type="text" value={this.state.remark} onChange={this.handleChangeRemark}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Access to Database:</Label>
                                <Input type="text" value={this.state.hasAccessToDB} onChange={this.handleChangeHasAccessToDB}/>
                            </FormGroup>
                            <FormGroup>
                                <Button type ="submit" value="submit " onClick={this.handleAddEmployee} >submit</Button> {' '}
                            </FormGroup>
                        </Form>
                    </Col>
                </Row>
            </div>
        )
    }
}


export default EmployeeRegistrationForm
