import {Button, makeStyles, TextField} from "@material-ui/core";

import React, {useEffect, useState} from "react";
import axios from "axios";
import {Form, Row, Col, Input, Label, FormGroup} from "reactstrap";

import {useHistory} from "react-router-dom";
import HeaderCommon from "../headers/HeaderCommon";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 20
    },
    buttonSignin : {
        marginTop : 30,
        marginLeft :70
    },
    buttonSignup :{
        marginTop :30,
        marginLeft :110
    },
    form :{
        alignContent : "center"
    },
    boxBorder :{
        border : "gray 5px solid",
        marginTop:10
    },
}));

const Signin = ()=>{
    const baseUrl = "http://localhost:8080";

    const history =useHistory();
    const classes = useStyles();

    const [isSubmitClicked, setIsSubmitClicked] = useState(false);

    const [companyId, setCompanyId] = useState("");
    const [password, setPassword] = useState("");


    useEffect(()=>{
        if(isSubmitClicked === true){
            axios.post("http://localhost:8080/admin/signin",

                {
                    "companyId" : companyId,
                    "password" : password
                }

            ).then((response) => {
                const {data} = response
                console.log(data);
                console.log(response.data)
                alert("successfully logged in")
                history.push("/admin/work");
            })
            .catch((error) => {
                console.log(error);
                alert(error);
            })
        }

    },[companyId,password]);

    return(
        <>
            <HeaderCommon/>
<div className={classes.boxBorder}>
            <Row>
                <Col lg={
                    {offset:4,size:3}
                } md={4} sm={6} xs={12}
                >
                    <Form className={classes.form}>

                        <FormGroup>
                            <Label>Company Id:</Label>
                            <Input
                                type = {"number"}
                                autoFocus = {true}
                                className = {classes.TextField}
                                label = {"Company Id"}
                                placeholder = {"1234"}
                                variant = {"outlined"}
                                value = {companyId}
                                onChange = {(event)=>{
                                    setCompanyId(event.target.value);
                                }}
                            />
                        </FormGroup>

                        <FormGroup>
                            <Label>Password:</Label>
                            <Input
                                type = {"number"}
                                autoFocus = {true}
                                className = {classes.TextField}
                                label = {"Company Id"}
                                placeholder = {"P@#er50jJ"}
                                variant = {"outlined"}
                                value = {password}
                                onChange = {(event)=>{
                                    setPassword(event.target.value);
                                }}
                            />
                        </FormGroup>

                        <FormGroup>
                            <Button

                                className = {classes.buttonSignup}
                                variant = {"contained"}
                                color = {"primary"}
                                onClick = {()=> history.push("/admin/work")}
                            >
                                sign in
                            </Button>
                        </FormGroup>
                    </Form>
                </Col>
            </Row>
</div>
        </>
    )
}
export default Signin
