import Axios from 'axios'
import {useState} from "react";

function SignupExa (){

    const url=""


    const[data, setData] = useState({
        name:"", date:"", userId:""

    })
    function handle(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
        console.log(newData)
    }
    function handleSubmit(e){
        e.preventDefault();
        Axios.post(url,{
            name:data.name,
            date:data.date,
            userId:data.userId
        }).then(res=>{
            console.log(res.data)
        }).catch(err=>{
            console.log(err)
        })
    }

    return(
        <form onSubmit={(e)=>handleSubmit(e)}>
            <input onChange={(e)=>handle(e)} id={"name"} value={data.name} type={"text"}/>
            <input onChange={(e)=>handle(e)} id={"date"} value={data.date} type={"date"}/>
            <input onChange={(e)=>handle(e)} id={"userId"} value={data.userId} type={"number"}/>
            <button>SUBMIt</button>
        </form>
    )
}
