import React, {useState} from "react";
import {useHistory} from "react-router-dom";
import HeaderforAdminPage from "../headers/HeaderforAdminpage";
import {makeStyles} from "@material-ui/core";

const useStyles = makeStyles(()=>({
    margin :{
        margin:20
    },
}));

const AdminWorkRouter =()=> {
    const classes = useStyles()
    const history = useHistory()

    const[data, setData] = useState({

        id:"id",

    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
    }
    return(
        <>
            <HeaderforAdminPage/>
        <div className={classes.margin}>
            <form>
                <label>Id of the  material:</label>{ }
                <input id={"id"} value={data.id} onChange={(e)=>handleFields(e)}/>
                <button onClick={history.push("material/list/"+data.id)}>
                    find</button>
            </form>





            <h4> <a href={"/employee/add"}>  add employee to our database </a> </h4>
            <h4> <a href={"/employee/list"}> list all employee details </a> </h4>
            <h4> <a href={"/employee/find"}> find particular employee </a> </h4>
            <h4> <a href={"/employee/fired"}> find fired employees </a> </h4>

            <h4> <a href={"/customers/list"}>  list all customers information </a> </h4>
            <h4> <a href={"/customers/find"}>  find particular customer </a> </h4>

            <h4> <a href={"/admin/list"}>  list all admins </a> </h4>


            <h4> <a href={"/admin/find"}>  find particular admin by id </a> </h4>


            <h4> <a href={"/material/add"}>  add materials to our database </a> </h4>
            <h4> <a href={"/material/list"}>  list all materials in the store </a> </h4>
            <h4> <a href={"/material/list/{id}"}>  find material info </a> </h4>
            <h4> <a href={"/users/material/list"}>  users view material list </a> </h4>

            <h4> <a href={"/report/month"}>  last month's report </a> </h4>

        </div>
        </>
    );
}
export default AdminWorkRouter
