import React, { useEffect , useState} from 'react'
import { TextField, Button, Box} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 20
    },
    Button : {
        margin : 30
    },
    boxBorder :{
        border : "gray 5px solid"
    }
}));

const CustomerAddPage = ()=>{

    const classes = useStyles();

    const [isSubmitClicked, setIsSubmitClicked] = useState(false);

    const [firstName, setFirstName] = useState("");
    const [middleName, setMiddleName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [datePurchased, setDatePurchased] = useState("");
    const [itemsPurchased, setItemsPurchased] = useState("");

    useEffect(()=>{
        if(isSubmitClicked === true){
            axios.post("http://localhost:8080/customer/add",

                {
                    "firstName" : firstName,
                    "middleName" : middleName,
                    "lastName" : lastName,
                    "email" : email,
                    "phoneNumber" : phoneNumber,
                    "itemsPurchased" : itemsPurchased,
                    "datePurchased" : datePurchased
                }

            )
                .then((response) => {
                    const {data} = response
                    console.log(data);
                    console.log(response.data)
                    alert("Employee is Registered Successfully with info: "+data.message)
                })
                .catch((error) => {
                    console.log(error);
                    alert(error);
                })
        }

    },[firstName,middleName,lastName,email,phoneNumber,itemsPurchased,datePurchased]);

    return(
        <Box className={classes.boxBorder}>
        <Form>
            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"First Name"}
                placeholder = {"First Name"}
                variant = {"outlined"}
                value = {firstName}
                onChange = {(event)=>{
                    setFirstName(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Middle Name"}
                placeholder = {"middle Name"}
                variant = {"outlined"}
                value = {middleName}
                onChange = {(event)=>{
                    setMiddleName(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Last Name"}
                placeholder = {"Last Name"}
                variant = {"outlined"}
                value = {lastName}
                onChange = {(event)=>{
                    setLastName(event.target.value);
                }}
            />

            <TextField
                type = {"email"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Email"}
                placeholder = {"sth@sth.com"}
                variant = {"outlined"}
                value = {email}
                onChange = {(event)=>{
                    setEmail(event.target.value);
                }}
            />

            <TextField
                type = {"tel"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Mobile Phone Number"}
                placeholder = {"0912345678"}
                variant = {"outlined"}
                value = {phoneNumber}
                onChange = {(event)=>{
                    setPhoneNumber(event.target.value);
                }}
            />

            <TextField
                type = {"number"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Items Purchased"}
                placeholder = {"the tag number"}
                variant = {"outlined"}
                value = {itemsPurchased}
                onChange = {(event)=>{
                    setItemsPurchased(event.target.value);
                }}
            />

            <TextField
                type = {"date"}
                autoFocus = {true}
                className = {classes.TextField}
                variant = {"outlined"}
                value = {datePurchased}
                onChange = {(event)=>{
                    setDatePurchased(event.target.value);
                }}
            />

            <Button
                className = {classes.Button}
                variant = {"contained"}
                color = {"primary"}
                onClick = {()=> setIsSubmitClicked(true)}
            >
                save customer
            </Button>

        </Form>
        </Box>
    )
}

export default CustomerAddPage
