import {Button, makeStyles, TextField} from "@material-ui/core";
import React, {useEffect, useState} from "react";
import axios from "axios";
import {Form} from "reactstrap";
import {useHistory} from "react-router-dom";
import HeaderCommon from "../headers/HeaderCommon";
import useAxios from 'axios-hooks'

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 20
    },
    Button : {
        margin : 30
    }
}));

const Signup = ()=>{
    const baseUrl = "http://localhost:8080";

    const classes = useStyles();

    let history = useHistory();
    const [isSubmitClicked, setIsSubmitClicked] = useState(false);

    const [companyId, setCompanyId] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const [{data, loading,error}, executePost] = useAxios(
        {
            url : 'http://localhost:8080/admin/add',
            method : 'POST'
        },
        {

        }
    );


    useEffect(()=>{
        if(isSubmitClicked === true){



            axios.post("http://localhost:8080/admin/signup/",

                {
                    "companyId" :companyId,
                    "password" : password,
                    "confirmPassword" : confirmPassword
                }

            )
                .then((response) => {
                    const {data} = response
                    console.log(response.data)
                    alert("you are Registered Successfully ,you are : "+data);
                    history.push("/");
                })
                .catch((error) => {
                    console.log(error);
                    alert(error.error);
                })


        }

    },[companyId,password,confirmPassword]);

    return(
        <>
            <HeaderCommon/>
            <Form>
                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Company Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {companyId}
                    onChange = {(event)=>{
                        setCompanyId(event.target.value);
                    }}
                />

                <TextField
                    type = {"password"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Password"}
                    placeholder = {"MixLike(P@#er50jJ)"}
                    variant = {"outlined"}
                    value = {password}
                    onChange = {(event)=>{
                        setPassword(event.target.value);
                    }}
                />

                <TextField
                    type = {"password"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Confirm Password"}
                    placeholder = {"the same as above)"}
                    variant = {"outlined"}
                    value = {confirmPassword}
                    onChange = {(event)=>{
                        setConfirmPassword(event.target.value);
                    }}
                />

                <Button
                    className = {classes.Button}
                    variant = {"contained"}
                    color = {"primary"}
                    onClick = {()=> {
                        if(password != confirmPassword){
                            alert("the passwords are different!!");
                        }
                        else {
                            setIsSubmitClicked(true)
                        }
                    }}
                >
                    sign up
                </Button>

            </Form>
        </>

    )
}
export default Signup
