import React, { useEffect , useState} from 'react'
import { TextField, Button, Box} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import {useHistory} from "react-router-dom";
import Axios from "axios";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 20
    },
    Button : {
        margin : 30
    },
    boxBorder :{
        border : "gray 5px solid"
    }
}));

const CustomerAddPage = ()=>{

    const url = "http://localhost:8080/customer/add";

    const classes = useStyles();

    let history = useHistory();

    const[data, setData] = useState({

        firstName:"", middleName:"", lastName:"",
        email:"", phoneNumber:"", itemsPurchased:""


    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
    }
    function handleSubmit(e){
        e.preventDefault();
        alert("called")
        Axios.post(url,{
            firstName:data.firstName,
            middleName:data.middleName,
            lastName:data.lastName,
            email:data.email,
            phoneNumber:data.phoneNumber,
            itemsPurchased:data.itemsPurchased
        }).then(res=>{
            alert(res.data + " is successfully added")
        }).catch(err=>{
            alert("Error while adding customer: "+err)

        })
    }
    return(
        <Box className={classes.boxBorder}>
        <Form>
            <TextField
                type = {"text"}
                id={"firstName"}
                required = {true}
                className = {classes.TextField}
                label = {"First Name"}
                placeholder = {"First Name"}
                variant = {"outlined"}
                value = {data.firstName}
                onChange={(e)=>handleFields(e)}

            />

            <TextField
                type = {"text"}
                id={"middleName"}
                required = {true}
                className = {classes.TextField}
                label = {"Middle Name"}
                placeholder = {"middle Name"}
                variant = {"outlined"}
                value = {data.middleName}
                onChange={(e)=>handleFields(e)}

            />

            <TextField
                type = {"text"}
                id={"lastName"}
                required = {true}
                className = {classes.TextField}
                label = {"Last Name"}
                placeholder = {"Last Name"}
                variant = {"outlined"}
                value = {data.lastName}
                onChange={(e)=>handleFields(e)}

            />

            <TextField
                type = {"email"}
                id={"email"}
                className = {classes.TextField}
                label = {"Email"}
                placeholder = {"sth@sth.com"}
                variant = {"outlined"}
                value = {data.email}
                onChange={(e)=>handleFields(e)}

            />

            <TextField
                type = {"tel"}
                id={"phoneNumber"}
                required = {true}
                className = {classes.TextField}
                label = {"Mobile Phone Number"}
                placeholder = {"0912345678"}
                variant = {"outlined"}
                value = {data.phoneNumber}
                onChange={(e)=>handleFields(e)}

            />

            <TextField
                type = {"number"}
                id={"itemsPurchased"}
                required = {true}
                className = {classes.TextField}
                label = {"Items Purchased(id)"}
                placeholder = {"the Id"}
                variant = {"outlined"}
                value = {data.itemsPurchased}
                onChange={(e)=>handleFields(e)}

            />

            <Button
                className = {classes.Button}
                variant = {"contained"}
                color = {"primary"}
                onClick = {(e)=> handleSubmit(e)}
            >
                save customer
            </Button>

        </Form>
        </Box>
    )
}

export default CustomerAddPage
