import React, { useEffect , useState} from 'react'
import { TextField, Button} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import CustomerAddPage from "./CustomerAddPage";
import HeaderInPurchaserPage from "../../headers/HeaderInPurchaserPage";
import CustomersMaterialListPage from "../../tables/usersPages/CustomersMaterialListPage";
import SalesEntryBar from "./SalesEntryBar";
import SellerMaterialList from "./SellerMaterialList";
import {useHistory} from "react-router-dom";
import Axios from "axios";
import HeaderInAdminNSalesPage from "../../headers/HeaderInAdminNSalesPage";
import EmployeeAddPage from "../EmployeeAddPage";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 15
    },
    Button : {
        margin : 22
    },
    boxBorder :{
        border : "gray 5px solid",
        marginTop:10
    },
    header :{
        backgroundColor : "blue"
    }

}));

const SalesAddPage = ()=>{

    const url = "http://localhost:8080/sales/add";

    const classes = useStyles();

    let history = useHistory();

    const[data, setData] = useState({
        customerId:"",itemId:"",quantity:"",price:""
    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
    }
    function handleSubmit(e){
        e.preventDefault();
        Axios.post(url,{
            customerId:data.customerId,
            itemId:data.itemId,
            quantity:data.quantity,
            price:data.price
        }).then(res=>{
            alert(res.data + " is successfully added")
        }).catch(err=>{
            alert("Error while adding customer: "+err)

        })
    }
    return(
        <>
            <HeaderInAdminNSalesPage/>

            <CustomerAddPage/>
            <hr/>
            <h1 className={classes.header}>Sales Data Entry Fields</h1>
            <hr/>

        <div className={classes.boxBorder}>
            <Form>
                <TextField
                    type = {"number"}
                    id={"customerId"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Customer Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {data.customerId}
                    onChange={(e)=>handleFields(e)}
                />
                <TextField
                    type = {"number"}
                    id={"itemId"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Item Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {data.itemId}
                    onChange={(e)=>handleFields(e)}
                />

                <TextField
                type = {"number"}
                id={"quantity"}
                required = {true}
                className = {classes.TextField}
                label = {"Quantity"}
                placeholder = {"1"}
                variant = {"outlined"}
                value = {data.quantity}
                onChange={(e)=>handleFields(e)}
                />

                <TextField
                    type = {"number"}
                    id={"price"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Price"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {data.price}
                    onChange={(e)=>handleFields(e)}
                />

                <Button
                    className = {classes.Button}
                    variant = {"contained"}
                    color = {"primary"}
                    onClick = {(e)=> handleSubmit(e)}
                >
                    sell
                </Button>

            </Form>
        </div>
            <SellerMaterialList/>
        </>

    )
}

export default SalesAddPage
