import {Button, makeStyles, TextField} from "@material-ui/core";
import React, {useEffect, useState} from "react";
import axios from "axios";
import {Form} from "reactstrap";
import {useHistory} from "react-router-dom";

const useStyles = makeStyles(()=>{});

const Signup = ()=>{
    const baseUrl = "http://localhost:8080";

    const classes = useStyles();

    const [isSubmitClicked, setIsSubmitClicked] = useState(false);

    const [firstName, setFirstName] = useState("");
    const [middleName, setMiddleName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    useEffect(()=>{
        if(isSubmitClicked === true){
            axios.post("http://localhost:8080/signup",

                {
                    "firstName" : firstName,
                    "middleName" : middleName,
                    "lastName" : lastName,
                    "email" : email,
                    "phoneNumber" : phoneNumber,
                    "password" : password,
                    "confirmPassword" : confirmPassword
                }

            )
                .then((response) => {
                    const {data} = response
                    console.log(data);
                    console.log(response.data)
                    alert("you are Registered Successfully with info: "+data.message);
                    useHistory.push("/");
                })
                .catch((error) => {
                    console.log(error);
                    alert(error);
                })
        }

    },[firstName,middleName,lastName,email,phoneNumber,password,confirmPassword]);

    return(
        <Form>
            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"First Name"}
                placeholder = {"First Name"}
                variant = {"outlined"}
                value = {firstName}
                onChange = {(event)=>{
                    setFirstName(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Middle Name"}
                placeholder = {"middle Name"}
                variant = {"outlined"}
                value = {middleName}
                onChange = {(event)=>{
                    setMiddleName(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Last Name"}
                placeholder = {"Last Name"}
                variant = {"outlined"}
                value = {lastName}
                onChange = {(event)=>{
                    setLastName(event.target.value);
                }}
            />

            <TextField
                type = {"email"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Email"}
                placeholder = {"sth@sth.com"}
                variant = {"outlined"}
                value = {email}
                onChange = {(event)=>{
                    setEmail(event.target.value);
                }}
            />

            <TextField
                type = {"tel"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Mobile Phone Number"}
                placeholder = {"0912345678"}
                variant = {"outlined"}
                value = {phoneNumber}
                onChange = {(event)=>{
                    setPhoneNumber(event.target.value);
                }}
            />

            <TextField
                type = {"password"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Password"}
                placeholder = {"MixLike(P@#er50jJ)"}
                variant = {"outlined"}
                value = {password}
                onChange = {(event)=>{
                    setPassword(event.target.value);
                }}
            />

            <TextField
                type = {"password"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Confirm Password"}
                placeholder = {"the same as above)"}
                variant = {"outlined"}
                value = {confirmPassword}
                onChange = {(event)=>{
                    setConfirmPassword(event.target.value);
                }}
            />

            <Button
                className = {classes.Button}
                variant = {"contained"}
                color = {"primary"}
                onClick = {()=> setIsSubmitClicked(true)}
            >
                set my data
            </Button>

        </Form>
    )
}
export default Signup
