import React, { useEffect , useState} from 'react'
import { TextField, Button} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import CustomerAddPage from "./CustomerAddPage";
import HeaderInAdminPage from "../../headers/HeaderInAdminPage";
import CustomersMaterialListPage from "../../tables/usersPages/CustomersMaterialListPage";
import SalesEntryBar from "./SalesEntryBar";
import SellerMaterialList from "./SellerMaterialList";
import {useHistory} from "react-router-dom";
import Axios from "axios";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 15
    },

    Button : {
        margin : 18
    },
    boxBorder :{
        border : "gray 5px solid",
        marginTop:10
    },

}));

const SalesAddPage = ()=>{

    const url = "http://localhost:8080/sales/add";

    const classes = useStyles();

    let history = useHistory();

    const[data, setData] = useState({

        itemId:"", sellerId:"", customerId:"",
        pricePp:"", noOfItem:""


    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
    }
    function handleSubmit(e){
        e.preventDefault();
        alert("called")
        Axios.post(url,{
            itemId:data.itemId,
            sellerId:data.sellerId,
            customerId:data.customerId,
            price:data.pricePp,
            noOfItem:data.noOfItem
        }).then(res=>{
            alert(res.data + " is successfully added")
        }).catch(err=>{
            alert("Error while adding customer: "+err)

        })
    }
    return(
        <>
            <HeaderInAdminPage/>

            <CustomerAddPage/>
            <hr/>
            <SalesEntryBar/>
            <hr/>

        <div className={classes.boxBorder}>
            <Form>
                <TextField
                    type = {"number"}
                    id={"itemId"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Item Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {data.itemId}
                    onChange={(e)=>handleFields(e)}
                />

                <TextField
                    type = {"number"}
                    id={"sellerId"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Seller Employee Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {data.sellerId}
                    onChange={(e)=>handleFields(e)}
                />

                <TextField
                    type = {"number"}
                    id={"customerId"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Customer Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {data.customerId}
                    onChange={(e)=>handleFields(e)}
                />
                <TextField
                    type = {"number"}
                    id={"price"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"price/p"}
                    placeholder = {"$40"}
                    variant = {"outlined"}
                    value = {data.pricePp}
                    onChange={(e)=>handleFields(e)}
                />

                />

                <Button
                    className = {classes.Button}
                    variant = {"contained"}
                    color = {"primary"}
                    onClick = {(e)=> handleSubmit(e)}
                >
                    sell
                </Button>

            </Form>
        </div>
            <SellerMaterialList/>
        </>

    )
}

export default SalesAddPage
