import Axios from 'axios'
import  {useState} from "react";
import {TextField} from "@mui/material";
import {Form} from "reactstrap";

function SignupExa (){

    const url=""


    const[data, setData] = useState({
        companyId:"", password:"", confirmPassword:""

    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
        console.log(newData)
    }
    function handleSubmit(e){
        e.preventDefault();
        Axios.post(url,{
            companyId:data.companyId,
            password:data.password,
            confirmPassword:data.confirmPassword
        }).then(res=>{
            console.log(res.data)
            console.log(data.companyId)
        }).catch(err=>{
            console.log(err)
            console.log(data.companyId)
        })
    }

    return(
        <Form onSubmit={(e)=>handleSubmit(e)}>

            <TextField onChange={(e)=>handleFields(e)} id={"name"} value={data.name} type={"number"}/>
            <TextField onChange={(e)=>handleFields(e)} id={"date"} value={data.password} type={"password"}/>
            <TextField onChange={(e)=>handleFields(e)} id={"userId"} value={data.confirmPassword} type={"password"}/>

            <button >SUBMIT</button>
        </Form>
    )
}
export default SignupExa
