import React, { useEffect , useState} from 'react'
import { TextField, Button} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import CustomerAddPage from "./CustomerAddPage";
import HeaderInAdminPage from "../../headers/HeaderInAdminPage";
import UsersMaterialListPage from "../../tables/usersPages/UsersMaterialListPage";
import SalesEntryBar from "./SalesEntryBar";
import SellerMaterialList from "./SellerMaterialList";
import {useHistory} from "react-router-dom";
import Axios from "axios";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 15
    },

    Button : {
        margin : 18
    },
    boxBorder :{
        border : "gray 5px solid",
        marginTop:10
    },

}));

const SalesAddPage = ()=>{

    const url = "http://localhost:8080/sales/add";

    const classes = useStyles();

    let history = useHistory();

    const[data, setData] = useState({

        itemId:"", sellerEmployeeCompampanyId:"", customerId:"",
        pricePp:""


    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
    }
    function handleSubmit(e){
        e.preventDefault();
        alert("called")
        Axios.post(url,{
            firstName:data.firstName,
            middleName:data.middleName,
            lastName:data.lastName,
            email:data.email,
            phoneNumber:data.phoneNumber,
            itemsPurchased:data.itemsPurchased,
            noOfItem:data.noOfItem
        }).then(res=>{
            alert(res.data + " is successfully added")
        }).catch(err=>{
            alert("Error while adding customer: "+err)

        })
    }
    return(
        <>
            <HeaderInAdminPage/>

            <CustomerAddPage/>
            <hr/>
            <SalesEntryBar/>
            <hr/>

        <div className={classes.boxBorder}>
            <Form>
                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Item Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {itemId}
                    onChange = {(event)=>{
                        setItemId(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Seller Employee Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {sellerEmployeeId}
                    onChange = {(event)=>{
                        setSellerEmployeeId(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Customer Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {sellerEmployeeId}
                    onChange = {(event)=>{
                        setSellerEmployeeId(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Price Sold At"}
                    placeholder = {"$4000.00"}
                    variant = {"outlined"}
                    value = {price}
                    onChange = {(event)=>{
                        setPrice(event.target.value);
                    }}
                />

                <Button
                    className = {classes.Button}
                    variant = {"contained"}
                    color = {"primary"}
                    onClick = {()=> setIsSubmitClicked(true)}
                >
                    sell
                </Button>

            </Form>
        </div>
            <SellerMaterialList/>
        </>

    )
}

export default SalesAddPage
