import {Button, makeStyles, TextField} from "@material-ui/core";

import React, {useEffect, useState} from "react";
import axios from "axios";
import {Form, Row, Col, Input, Label, FormGroup} from "reactstrap";

import {useHistory} from "react-router-dom";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 20
    },
    buttonSignin : {
        marginTop : 30,
        marginLeft :70
    },
    buttonSignup :{
        marginTop :30,
        marginLeft :110
    },
    form :{
        alignContent : "center"
    }
}));

const Signin = ()=>{
    const baseUrl = "http://localhost:8080";

    const history =useHistory();
    const classes = useStyles();

    const [isSubmitClicked, setIsSubmitClicked] = useState(false);

    const [id, setId] = useState("");
    const [password, setPassword] = useState("");


    useEffect(()=>{
        if(isSubmitClicked === true){
            axios.post("http://localhost:8080/signin",

                {
                    "id" : id,
                    "password" : password
                }

            )
                .then((response) => {
                    const {data} = response
                    console.log(data);
                    console.log(response.data)
                    alert("successfully logged in")
                    history.push("/admin/signin/domywork");
                })
                .catch((error) => {
                    console.log(error);
                    alert(error);
                })
        }

    },[id,password]);

    return(
        <>
                <Row>
                    <Col lg={
                        {offset:4,size:3}
                    } md={4} sm={6} xs={12}
                    >
                        <Form className={classes.form}>
                            <FormGroup>
                                <Label>Company Id:</Label>
                                <Input
                                    type = {"number"}
                                    autoFocus = {true}
                                    className = {classes.TextField}
                                    label = {"Company Id"}
                                    placeholder = {"1234"}
                                    variant = {"outlined"}
                                    value = {id}
                                    onChange = {(event)=>{
                                        setId(event.target.value);
                                    }}
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label>Password:</Label>
                                <Input
                                    type = {"number"}
                                    autoFocus = {true}
                                    className = {classes.TextField}
                                    label = {"Company Id"}
                                    placeholder = {"1234"}
                                    variant = {"outlined"}
                                    value = {id}
                                    onChange = {(event)=>{
                                        setId(event.target.value);
                                    }}
                                />
                            </FormGroup>
                            <FormGroup>
                                <Button

                                    className = {classes.buttonSignup}
                                    variant = {"contained"}
                                    color = {"primary"}
                                    onClick = {()=> history.push("/signup")}
                                >
                                    sign up
                                </Button>
                            </FormGroup>
                            <FormGroup>
                                <Button

                                    className = {classes.buttonSignup}
                                    variant = {"contained"}
                                    color = {"primary"}
                                    onClick = {()=> history.push("/signup")}
                                >
                                    sign up
                                </Button>
                            </FormGroup>
                            <Input
                                type = {"number"}
                                autoFocus = {true}
                                className = {classes.TextField}
                                label = {"Company Id"}
                                placeholder = {"1234"}
                                variant = {"outlined"}
                                value = {id}
                                onChange = {(event)=>{
                                    setId(event.target.value);
                                }}
                            />

                            <Input
                                type = {"password"}
                                autoFocus = {true}
                                className = {classes.TextField}
                                label = {"Password"}
                                placeholder = {"MixLike(P@#er50jJ)"}
                                variant = {"outlined"}
                                value = {password}
                                onChange = {(event)=>{
                                    setPassword(event.target.value);
                                }}
                            />


                        </Form>
                    </Col>


                </Row>
            <Button

                className = {classes.buttonSignup}
                variant = {"contained"}
                color = {"primary"}
                onClick = {()=> history.push("/signup")}
            >
                sign up
            </Button>
            <Button
                className = {classes.buttonSignin}
                variant = {"contained"}
                color = {"primary"}
                onClick = {()=> setIsSubmitClicked(true)}
            >
                sign in
            </Button>
        </>
    )
}
export default Signin
