import React, { useEffect , useState} from 'react'
import { TextField, Button, Box} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import {useHistory} from "react-router-dom";
import Axios from "axios";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 20
    },
    Button : {
        margin : 30
    },
    boxBorder :{
        border : "gray 5px solid"
    }
}));

const CustomerAddPage = ()=>{

    const url = "http://localhost:8080/customer/add";

    const classes = useStyles();

    let history = useHistory();

    const[data, setData] = useState({

        firstName:"", middleName:"", lastName:"",
        birthDate:"", email:"", phoneNumber:"",
        position:"",  role:""

    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
        console.log(newData)
    }
    function handleSubmit(e){
        e.preventDefault();
        alert("called")
        Axios.post(url,{
            firstName:data.firstName,
            middleName:data.middleName,
            lastName:data.lastName,
            position:data.position,
            role:data.role,
            email:data.email,
            phoneNumber:data.phoneNumber,

        }).then(res=>{
            alert(res.data + " is successfully added")
        }).catch(err=>{
            alert("Error while adding customer: "+err)

        })
    }
    return(
        <Box className={classes.boxBorder}>
        <Form>
            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"First Name"}
                placeholder = {"First Name"}
                variant = {"outlined"}
                value = {firstName}
                onChange = {(event)=>{
                    setFirstName(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Middle Name"}
                placeholder = {"middle Name"}
                variant = {"outlined"}
                value = {middleName}
                onChange = {(event)=>{
                    setMiddleName(event.target.value);
                }}
            />

            <TextField
                type = {"text"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Last Name"}
                placeholder = {"Last Name"}
                variant = {"outlined"}
                value = {lastName}
                onChange = {(event)=>{
                    setLastName(event.target.value);
                }}
            />

            <TextField
                type = {"email"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Email"}
                placeholder = {"sth@sth.com"}
                variant = {"outlined"}
                value = {email}
                onChange = {(event)=>{
                    setEmail(event.target.value);
                }}
            />

            <TextField
                type = {"tel"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Mobile Phone Number"}
                placeholder = {"0912345678"}
                variant = {"outlined"}
                value = {phoneNumber}
                onChange = {(event)=>{
                    setPhoneNumber(event.target.value);
                }}
            />

            <TextField
                type = {"number"}
                autoFocus = {true}
                className = {classes.TextField}
                label = {"Items Purchased"}
                placeholder = {"the tag number"}
                variant = {"outlined"}
                value = {itemsPurchased}
                onChange = {(event)=>{
                    setItemsPurchased(event.target.value);
                }}
            />

            <TextField
                type = {"date"}
                autoFocus = {true}
                className = {classes.TextField}
                variant = {"outlined"}
                value = {datePurchased}
                onChange = {(event)=>{
                    setDatePurchased(event.target.value);
                }}
            />

            <Button
                className = {classes.Button}
                variant = {"contained"}
                color = {"primary"}
                onClick = {()=> setIsSubmitClicked(true)}
            >
                save customer
            </Button>

        </Form>
        </Box>
    )
}

export default CustomerAddPage
