import {Button, makeStyles, TextField} from "@material-ui/core";

import React, {useEffect, useState} from "react";
import axios from "axios";
import {Form} from "reactstrap";

import {useHistory} from "react-router-dom";
import HeaderCommon from "../headers/HeaderCommon";
import Axios from "axios";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 15,

    },
    buttonSignup :{
        marginTop :22,
        marginLeft :110
    },

    boxBorder :{
        border : "gray 5px solid",
        marginTop:10
    },
}));

const SignInPage = ()=>{
    const url = "http://localhost:8080/authorizedEmployee/login";

    const history =useHistory();
    const classes = useStyles();

    const[data, setData] = useState({
        companyId:"",
        password:"",
    })

    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
    }

    function handleSubmit(e){
        e.preventDefault();
        Axios.post(url,{
            companyId:data.companyId,
            password:data.password,
        }).then(res=>{
            alert("successfully logged in as: "+ res.data)
            history.push("/admin/work") //cccccccccccccccccccccccccccccccccc
        }).catch(err=>{
            alert(err+"either your password or your user id is incorrect"+"\n or you are not an authorized user")
        })
    }

    return(
        <>
            <HeaderCommon/>
            <div className={classes.boxBorder}>

                <Form className={classes.form}>

                    <TextField
                        type = {"number"}
                        id={"companyId"}
                        required = {true}
                        className = {classes.TextField}
                        label = {"Company Id"}
                        placeholder = {"1234"}
                        variant = {"outlined"}
                        value = {data.companyId}
                        onChange = {(e)=>handleFields(e)}
                    />

                    <TextField
                        type = {"number"}
                        id={"password"}
                        required = {true}
                        className = {classes.TextField}
                        label = {"Password"}
                        placeholder = {"P@#er50jJ"}
                        variant = {"outlined"}
                        value = {data.password}
                        onChange = {(e)=>handleFields(e)}
                    />

                    <Button
                        className = {classes.buttonSignup}
                        variant = {"contained"}
                        color = {"primary"}
                        onClick = {(e)=> handleSubmit(e)}
                    >
                        sign in
                    </Button>
                </Form>

            </div>
        </>
    )
}
export default SignInPage
