import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button ,Label, Form, Input, Alert,Navbar,NavbarBrand,NavItem,NavbarToggler,
    Nav, NavLink, NavbarText,Collapse, FormGroup, Row,Col} from 'reactstrap';
import {Dropdown, DropdownItem,DropdownMenu,DropdownToggle} from 'reactstrap'
import axios from 'axios'

const instance = axios.create({
    baseURL:'http://localhost:8080',
    headers:{'Contente-Type':'application/json'}
})

class AssessorForm extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            username:"",
            first_name:"",
            last_name:"",
            email:"",
            password:""
        }
        this.handleChangeUserName = this.handleChangeUserName.bind(this)
        this.handleChangeFirstName = this.handleChangeFirstName.bind(this)
        this.handleChangeLastName = this.handleChangeLastName.bind(this)
        this.handleChangeEmail = this.handleChangeEmail.bind(this)
        this.handleChangePassword = this.handleChangePassword.bind(this)

        this.handleSignUpForm = this.handleSignupForm.bind(this)
    }

    handleSignupForm(){
        instance({
            'method':'POST',
            'url':'/api/signup',
            'data':{
                username:this.state.username,
                first_name:this.state.first_name,
                last_name:this.state.last_name,
                email:this.state.email,
                password:this.state.password
            }
        })
    }
    handleLogin(){
        instance({
            'method':'POST',
            'url':'/api/login',
            'data':{
                username:this.state.username,
                password:this.state.password
            }
        })
    }

    handleChangeUserName(event){
        const username = event.target.value;
        this.setState({username:username})
    }
    handleChangeFirstName(event){
        const first_name = event.target.value;
        this.setState({first_name:first_name})
    }
    handleChangeLastName(event){
        const last_name = event.target.value;
        this.setState({last_name:last_name})
    }
    handleChangeEmail(event){
        const email = event.target.value;
        this.setState({email:email})
    }
    handleChangePassword(event){
        const password = event.target.value;
        this.setState({password:password})
    }

    render(){
        return(
            <div>
                <Row>
                    <Col lg={
                        {offset:4,size:3}
                    } md={4} sm={6} xs={12}
                    >

                        <Form onSubmit={this.handleSignupForm}>
                            <FormGroup>
                                <Label>Username:</Label>
                                <Input type="text" value={this.state.username} onChange={this.handleChangeUserName}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>First Name:</Label>
                                <Input type="text" value={this.state.first_name} onChange={this.handleChangeFirstName}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Last Name:</Label>
                                <Input type="text" value={this.state.last_name} onChange={this.handleChangeLastName}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Email:</Label>
                                <Input type="text" value={this.state.email} onChange={this.handleChangeEmail}/>
                            </FormGroup>
                            <FormGroup>
                                <Label>Password:</Label>
                                <Input type="text" value={this.state.password} onChange={this.handleChangePassword}/>
                            </FormGroup>
                            <FormGroup>
                                <Button type ="submit" value="submit " onClick={this.handleSignupForm} >submit</Button> {' '}
                                <Button value="submit " onClick={this.handleLogin} style={{marginLeft:20}}>login</Button>
                            </FormGroup>
                        </Form>
                    </Col>
                </Row>
            </div>
        )
    }
}


export default AssessorForm
