import React, { useEffect , useState} from 'react'
import { TextField, Button} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import CustomerAddPage from "./CustomerAddPage";
import HeaderInAdminPage from "../../headers/HeaderInAdminPage";
import CustomersMaterialListPage from "../../tables/usersPages/CustomersMaterialListPage";
import SalesEntryBar from "./SalesEntryBar";
import SellerMaterialList from "./SellerMaterialList";
import {useHistory} from "react-router-dom";
import Axios from "axios";
import HeaderForAdminPage from "../../headers/HeaderForAdminPage";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 15
    },

    Button : {
        margin : 22
    },
    boxBorder :{
        border : "gray 5px solid",
        marginTop:10
    },

}));

const SalesAddPage = ()=>{

    const url = "http://localhost:8080/sales/add";

    const classes = useStyles();

    let history = useHistory();

    const[data, setData] = useState({

        customerPhoneNumber:""

    })
    function handleFields(e){
        const newData = {...data}
        newData[e.target.id] =e.target.value;
        setData(newData);
    }
    function handleSubmit(e){
        e.preventDefault();
        Axios.post(url,{

            customerId:data.customerId,
            customerPhoneNumber:data.customerPhoneNumber

        }).then(res=>{
            alert(res.data + " is successfully added")
        }).catch(err=>{
            alert("Error while adding customer: "+err)

        })
    }
    return(
        <>
            <HeaderForAdminPage/>

            <CustomerAddPage/>
            <hr/>
            <SalesEntryBar/>
            <hr/>

        <div className={classes.boxBorder}>
            <Form>

                <TextField
                    type = {"tel"}
                    id={"customerPhoneNumber"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Customer Phone Number"}
                    placeholder = {"0910231212"}
                    variant = {"outlined"}
                    value = {data.customerPhoneNumber}
                    onChange={(e)=>handleFields(e)}
                />

                <TextField
                    type = {"number"}
                    id={"customerId"}
                    required = {true}
                    className = {classes.TextField}
                    label = {"Customer Id"}
                    placeholder = {"1"}
                    variant = {"outlined"}
                    value = {data.customerId}
                    onChange={(e)=>handleFields(e)}
                />

                <Button
                    className = {classes.Button}
                    variant = {"contained"}
                    color = {"primary"}
                    onClick = {(e)=> handleSubmit(e)}
                >
                    sell
                </Button>

            </Form>
        </div>
            <SellerMaterialList/>
        </>

    )
}

export default SalesAddPage
