import React, { useEffect , useState} from 'react'
import { TextField, Button} from '@material-ui/core'
import {Form, FormGroup} from "reactstrap";
import { makeStyles } from '@material-ui/core'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import HeaderforAdminPage from "../headers/HeaderforAdminpage";

const useStyles = makeStyles(()=>({
    TextField : {
        margin : 20
    },
    buttonSignin : {
        marginTop : 30,
            marginLeft :70
    },
    buttonSignup :{
        marginTop :30,
            marginLeft :110
    },
    form :{
        alignContent : "center"
    },
    Button :{
        margin:30
    }
}));

const MaterialAddPage = ()=>{

    //const baseUrl = "http://localhost:8080";

    const classes = useStyles();

    const [isSubmitClicked, setIsSubmitClicked] = useState(false);

    const [category, setCategory] = useState("");
    const [productCountry, setProductCountry] = useState("");
    const [productCompany, setProductCompany] = useState("");
    const [itemName, setItemName] = useState("");
    const [itemTagNum, setItemTagNum] = useState("");
    const [noOfItem, setNoOfItem] = useState("");
    const [cost, setCost] = useState("");
    const [standard, setStandard] = useState("");
    const [minSellingPrice, setMinSellingPrice] = useState("");
    const [purchaserId, setPurchaserId] = useState("");


    useEffect(()=>{
        if(isSubmitClicked === true){
            axios.post("http://localhost:8080/customer/add",

                {
                    "category" : category,
                    "productCountry" : productCountry,
                    "productCompany" : productCompany,
                    "itemName" : itemName,
                    "itemTagNum" : itemTagNum,
                    "noOfItem" : noOfItem,
                    "cost" : cost,
                    "standard" : standard,
                    "purchaserId" : purchaserId

                    // "category":"Agricultural",
                    // "productCountry":"USA",
                    // "productCompany" :"ClAAS",
                    // "itemName":"tractor",
                    // "cost": 4000000.00,
                    // "itemTagNum" : "0003/18",
                    // "minSellingPrice": 500000.00,
                    // "purchaser":2,
                    // "noOfItem" :50,
                    // "standard": "ISO-9001/2020"
                }

            ).then((response) => {
                const {data} = response
                console.log(data)
                alert("Employee is Registered Successfully with info: "+response.data)

            }).catch((error) => {
                console.log(error);
                alert(error.error);
            })
        }

    }
    ,[category,productCountry,productCompany,itemName,itemTagNum,noOfItem,cost,standard,purchaserId,isSubmitClicked]);

    return(
        <>
            <HeaderforAdminPage/>

            <Form>
                <TextField
                    type = {"text"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Category"}
                    placeholder = {"Agriculture"}
                    variant = {"outlined"}
                    value = {category}
                    onChange = {(event)=>{
                        setCategory(event.target.value);
                    }}
                />

                <TextField
                    type = {"text"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Country Made Of"}
                    placeholder = {"product country"}
                    variant = {"outlined"}
                    value = {productCountry}
                    onChange = {(event)=>{
                        setProductCountry(event.target.value);
                    }}
                />

                <TextField
                    type = {"text"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Product Company"}
                    placeholder = {"JohnDeere"}
                    variant = {"outlined"}
                    value = {productCompany}
                    onChange = {(event)=>{
                        setProductCompany(event.target.value);
                    }}
                />

                <TextField
                    type = {"text"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Product Name"}
                    placeholder = {"Tractor"}
                    variant = {"outlined"}
                    value = {itemName}
                    onChange = {(event)=>{
                        setItemName(event.target.value);
                    }}
                />

                <TextField
                    type = {"text"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Product Tag Number"}
                    placeholder = {"0012/20"}
                    variant = {"outlined"}
                    value = {itemTagNum}
                    onChange = {(event)=>{
                        setItemTagNum(event.target.value);
                    }}
                />
                <TextField
                    type = {"text"}
                    autoFocus = {true}
                    label={"Standard"}
                    className = {classes.TextField}
                    variant = {"outlined"}
                    value = {standard}
                    onChange = {(event)=>{
                        setStandard(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    label={"Number Of Items"}
                    className = {classes.TextField}
                    variant = {"outlined"}
                    value = {noOfItems}
                    onChange = {(event)=>{
                        setNoOfItems(event.target.value);
                    }}
                />
                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    label={"Cost"}
                    className = {classes.TextField}
                    variant = {"outlined"}
                    value = {cost}
                    onChange = {(event)=>{
                        setCost(event.target.value);
                    }}
                />
                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    label={"Minimum Selling Price"}
                    className = {classes.TextField}
                    variant = {"outlined"}
                    value = {minSellingPrice}
                    onChange = {(event)=>{
                        setMinSellingPrice(event.target.value);
                    }}
                />

                <TextField
                    type = {"number"}
                    autoFocus = {true}
                    className = {classes.TextField}
                    label = {"Purchaser Employee Company Id"}
                    placeholder = {"employee company id"}
                    variant = {"outlined"}
                    value = {purchaserId}
                    onChange = {(event)=>{
                        setPurchaserId(event.target.value);
                    }}
                />

                <Button
                    className = {classes.Button}
                    variant = {"contained"}
                    color = {"primary"}
                    onClick = {()=> setIsSubmitClicked(true)}
                >
                    save product
                </Button>

            </Form>
        </>
    )
}

export default MaterialAddPage
